import React, { useState, useEffect } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css'
import MovieList from './components/MovieList';
import MovieListHeading from './components/MovieListHeading';
import Searchbox from './components/Searchbox';
import AddFavourite from './components/AddFavourites';
import RemoveFavourites from './components/RemoveFavourites';
import IMDB from './components/IMDB';
import Stream from './components/Stream';

function App() {
  const [movies, setMovies] = useState([])
  const [favourites, setFavourites] = useState([])
  const [searchValue, setSearchValue] = useState('')
  const [streamOptions, setStreamOptions] = useState({})

  const getMovieRequest = async (searchValue) => {
    const url = `http://www.omdbapi.com/?s=${searchValue}&apikey=4a9d1b9e`
    const response = await fetch(url)
    const responseJSON = await response.json()

    if(responseJSON.Search){
      setMovies(responseJSON.Search)
    }
  }

  const getFavouriteMovies = () => {
    const favouriteList = JSON.parse(localStorage.getItem('react-movie-app-favourites'))
    setFavourites(favouriteList)
  }

  useEffect(() => {
    getMovieRequest(searchValue)
  }, [searchValue])

  useEffect(() => {
    getFavouriteMovies()
  }, [])

  const saveToLocalStorage = (items) => {
    localStorage.setItem('react-movie-app-favourites', JSON.stringify(items))
  }

  const addFavouriteMovie = async (movie) => {
    const newFavouriteList = [...favourites, movie]
    setFavourites(newFavouriteList)
    saveToLocalStorage(newFavouriteList)
  }

  const removeFavouriteMovie = (movie) => {
    const newFavouriteList = favourites.filter((favourite) => (
      favourite.imdbID !== movie.imdbID
    ))
    setFavourites(newFavouriteList)
    saveToLocalStorage(newFavouriteList)
  }

  const showIMDBInfo = async (movie) => {
    const url = `https://www.omdbapi.com/?apikey=4a9d1b9e&t="${movie.Title}"`
    const response = await fetch(url)
    const responseJSON = await response.json()

    if(responseJSON.imdbID){
      const imdbUrl = `https://www.imdb.com/title/${responseJSON.imdbID}`
      window.open(imdbUrl)
    }
  }

  const streamMovie = (movie) => {
    console.log(streamOptions)
  }

  const updateStreamOptions = async (movie) => {
    const url = `https://streaming-availability.p.rapidapi.com/v2/get/basic?country=us&imdb_id=${movie.imdbID}&output_language=en`;
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': 'f2a0cca2c0msh2fcdb5a6dea7a0cp1391a0jsnc637e3de8a32',
        'X-RapidAPI-Host': 'streaming-availability.p.rapidapi.com'
      }
    };

    try {
      const response = await fetch(url, options);
      const responseJSON = await response.json();
      const streamingInfo = responseJSON.result.streamingInfo.us
      setStreamOptions(streamingInfo)
      //console.log(streamOptions)
      //console.log(streamingInfo)
      //console.log(streamingInfo.hbo[0].link);
      /*streamOptions.forEach((item) => {
        console.log(item[0].link)
        console.log('a')
      })*/
      //console.log(streamOptions[0][0].link)
    } catch (error) {
      console.error(error);
    }
  }

  const getStreamOptions = (movie) => {
    updateStreamOptions(movie).
    then(streamMovie(movie))
  }

  return (
    <div className="container-fluid movie-app">
      <div className='row d-flex align-items-center mt-4 mb-4'>
        <MovieListHeading heading='Movies & Series'/>
        <Searchbox searchValue={searchValue} setSearchValue={setSearchValue}/>
      </div>
      <div className="row">
