import React from 'react'
import Dropdown from 'react-bootstrap/Dropdown'


const Drop = (props) => {
    //const StreamOptions = props.streamOptions
    const StreamOptions = Object.entries(props.streamOptions)

    return(
        <Dropdown>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
                Stream
            </Dropdown.Toggle>

            <Dropdown.Menu>
                {StreamOptions.map((option) => (
                    <Dropdown.Item href={option[1]}>{option[0].toUpperCase()}</Dropdown.Item>
                ))}
            </Dropdown.Menu>
        </Dropdown>
    )
}

export default Drop


